import { BaBackupFile } from './ba-backup-file';
import { BaBackupFileListJson } from './__json__/ba-backup-file-list-json';
import { BaBackupFilterParams } from '../ba-backup-filter-params';

/**
 * BaBackupFileList class
 */

/* istanbul ignore next: autogenerated */
export class BaBackupFileList {

  constructor(private _json: BaBackupFileListJson) {
  }

  /**
   * Get current page parameters.
   * @returns {BaBackupFilterParams}
   */
  get currentPageParameters(): BaBackupFilterParams {
    return new BaBackupFilterParams(this._json.current_page_parameters);
  }

  /**
   * Get next page parameters.
   * @returns {BaBackupFilterParams}
   */
  get nextPageParameters(): BaBackupFilterParams {
    return new BaBackupFilterParams(this._json.next_page_parameters);
  }

  /**
   * Get total records.
   * @returns {number}
   */
  get totalRecords(): number {
    return this._json.total_records;
  }

  /**
   * Get last page.
   * @returns {boolean}
   */
  get lastPage(): boolean {
    return this._json.last_page;
  }

  /**
   * Get data.
   * @returns {Array<BaBackupFile>}
   */
  get data(): Array<BaBackupFile> {
    return this._json.data.map(baFile => new BaBackupFile(baFile));
  }

  /**
   * Get the json representation of this class.
   * @returns {BaBackupFileListJson}
   */
  get json(): BaBackupFileListJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
