import { DynamicMemberJson } from './__json__/dynamic-member-json';
import { CriteriaJson } from './__json__/criteria-json';
import { SecurityGroupOperatorType } from './__json__/security-group-operator-type';

/**
 * Dynamic Member.
 */
/* istanbul ignore next: autogenerated */
export class DynamicMember {

  constructor(private _json: DynamicMemberJson) {
  }

  /**
   * Get operator.
   * @returns {SecurityGroupOperatorType}
   */
  get operator(): SecurityGroupOperatorType {
    return this._json.operator;
  }

  /**
   * Get criteria.
   * @returns {Array<CriteriaJson>}
   */
  get criteria(): Array<CriteriaJson> {
    return this._json.criteria;
  }

  /**
   * Get the json representation of this class.
   * @returns {DynamicMemberJson}
   */
  get json(): DynamicMemberJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
