import { SecurityGroupJson } from './__json__/security-group-json';
import { SecurityGroupMember } from './securityGroupMember';
import { DynamicMember } from './dynamic-member';

/**
 * Security Group.
 */
/* istanbul ignore next: autogenerated */
export class SecurityGroup {

  constructor(private _json: SecurityGroupJson) {
  }

  /**
   * Get object id.
   * @returns {string}
   */
  get objectId(): string {
    return this._json.object_id;
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get description.
   * @returns {string}
   */
  get description(): string {
    return this._json.description;
  }

  /**
   * Get scope.
   * @returns {string}
   */
  get scope(): string {
    return this._json.scope;
  }

  /**
   * Get is temporal.
   * @returns {boolean}
   */
  get isTemporal(): boolean {
    return this._json.is_temporal;
  }

  /**
   * Get inheritance allowed.
   * @returns {boolean}
   */
  get inheritanceAllowed(): boolean {
    return this._json.inheritance_allowed;
  }

  /**
   * Get included member.
   * @returns {Array<SecurityGroupMember>}
   */
  get includedMember(): Array<SecurityGroupMember> {
    return this._json.included_member.map(it => new SecurityGroupMember(it));
  }

  /**
   * Get excluded member.
   * @returns {Array<SecurityGroupMember>}
   */
  get excludedMember(): Array<SecurityGroupMember> {
    return this._json.excluded_member.map(it => new SecurityGroupMember(it));
  }

  /**
   * Get dynamic member.
   * @returns {Array<DynamicMember>}
   */
  get dynamicMember(): Array<DynamicMember> {
    return this._json.dynamic_member ? this._json.dynamic_member.map(it => new DynamicMember(it)) : [];
  }

  /**
   * Get the json representation of this class.
   * @returns {SecurityGroupJson}
   */
  get json(): SecurityGroupJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
